mapper.update:
update t_board set 
board_tag_id = #{board_tag_id}, board_text = #{board_text}, board_title = #{board_title} 
where board_id = #{board_id}

mapper.select:
  		select count(*) as 'board_count' from t_board
  		
			where board_tag_id = #{board_tag_id}
		
		
			where board_title like CONCAT('%',#{keyword},'%') or board_text like CONCAT('%',#{keyword},'%') or member_id like CONCAT('%',#{keyword},'%')

select t_board.board_id as board_id, count(board_reply_id) as reply_count, t_board.member_id as member_id, board_title, board_text, board_hit, board_recommend, board_regdate, case when(board_tag_id=1) then '잡담' when(board_tag_id=2) then '공지' when(board_tag_id=3) then '캠핑팁' when(board_tag_id=4) then '상품후기' when(board_tag_id=5) then '캠핑후기' end as 'board_tag_name' from t_board left join t_board_reply on t_board.board_id = t_board_reply.board_id where (t_board.board_title like CONCAT('%',#{keyword},'%') or t_board.board_text like CONCAT('%',#{keyword},'%') or t_board.member_id like CONCAT('%',#{keyword},'%'))
and board_tag_id = ${board_tag_id}
and board_deleteYN = 'N' group by t_board.board_id order by t_board.board_id DESC limit ${page_count}, 10;
